import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import firebase from "../../config/fire-conf";
import Link from "../common/ActiveLink";
import { Menu, Dropdown, Popconfirm } from "antd";
import { v4 as uuidv4 } from "uuid";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import { ToastContainer, toast } from "react-toastify";

const useStyles = makeStyles((theme) => ({
  tableHead: {
    backgroundColor: "#00152921",
  },
}));

function DbSecrets(props) {
  const router = useRouter();
  const classes = useStyles();

  const newApiMenu = (
    <Menu>
      <Menu.Item disabled>Database</Menu.Item>
      {props.dbList.map((ele, index) => {
        return (
          <Menu.Item key={ele.dbId + index} onClick={() => addNewApi(index)}>
            {ele.dbName}
          </Menu.Item>
        );
      })}
    </Menu>
  );

  const addNewApi = (index) => {
    let modefiedObj = [...props.dbList];
    let changes = [];
    if (modefiedObj[index].apiSecretKeys) {
      changes = [...modefiedObj[index].apiSecretKeys, uuidv4()];
    } else {
      changes = [uuidv4()];
    }
    modefiedObj[index].apiSecretKeys = changes;
    props.setdbList(modefiedObj);

    //updating data in firebase
    firebase
      .firestore()
      .doc(
        "usersData/" +
          props.userDataRedux.uid +
          "/Databases/" +
          props.dbList[index].dbId
      )
      .update({
        apiSecretKeys: changes,
      });
  };

  function confirmDelete(index1, index2) {
    let modefiedObj = [...props.dbList];
    let changes = [];

    if (
      modefiedObj[index1].apiSecretKeys &&
      modefiedObj[index1].apiSecretKeys.length > 1
    ) {
      changes = modefiedObj[index1].apiSecretKeys.splice(index2, 1);
    }
    modefiedObj[index1].apiSecretKeys = changes;
    props.setdbList(modefiedObj);

    //updating data in firebase
    firebase
      .firestore()
      .doc(
        "usersData/" +
          props.userDataRedux.uid +
          "/Databases/" +
          props.dbList[index1].dbId
      )
      .update({
        apiSecretKeys: changes,
      });

    toast.info("Item deleted Successfully", {
      position: "bottom-left",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });
  }

  return (
    <section className="blog-details-area">
      <div className="row">
        <div className="col-lg-12 col-md-12" style={{ margin: "0px auto" }}>
          <div className="blog-details">
            <div className="article-content">
              <h5>Database Secrets</h5>
              <p>
                Api Secret Keys are used to authenticate user api. You can use
                Secret Api Keys with you User Api Key and Database Id to acess
                your MetaShard Database data.app receives this token and uses it
                to authenticate with Firebase.At least one secret must exist at
                all times
              </p>
              <div
                className="normal-button"
                style={{ float: "right", margin: "20px 0px 10px 0px" }}
              >
                <Dropdown overlay={newApiMenu} placement="bottomCenter">
                  <button>Add new Api Secret Key</button>
                </Dropdown>
              </div>

              <div style={{ margin: "30px 0px 20px 0px" }}>
                <TableContainer component={Paper}>
                  <Table className={classes.table} aria-label="simple table">
                    <TableHead
                      classes={{
                        root: classes.tableHead,
                      }}
                    >
                      <TableRow>
                        <TableCell>Database</TableCell>
                        <TableCell>Database Id</TableCell>
                        <TableCell>Secret Api Key</TableCell>
                        <TableCell></TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {props.dbList.map((dbEle, index1) => {
                        return (
                          dbEle.apiSecretKeys &&
                          dbEle.apiSecretKeys.map((apiKey, index2) => (
                            <TableRow key={apiKey + index2 + index1} hover>
                              <TableCell component="th" scope="row">
                                {dbEle.dbName}
                              </TableCell>
                              <TableCell>{dbEle.dbId}</TableCell>
                              <TableCell>{apiKey}</TableCell>
                              <TableCell>
                                <Popconfirm
                                  placement="top"
                                  title="Are you sure to delete this Api Secret Key?"
                                  onConfirm={() =>
                                    confirmDelete(index1, index2)
                                  }
                                  okText="Yes"
                                  cancelText="No"
                                >
                                  <IconButton size="small">
                                    <DeleteIcon fontSize="small" />
                                  </IconButton>{" "}
                                </Popconfirm>
                              </TableCell>
                            </TableRow>
                          ))
                        );
                      })}
                      {props.dbList &&
                        props.dbList.apiSecretKeys &&
                        props.dbList.apiSecretKeys.map((row, index) => (
                          <TableRow key={row}>
                            <TableCell component="th" scope="row">
                              {props.dbList[index].dbName}
                            </TableCell>
                            <TableCell>{props.dbList[index].dbId}</TableCell>
                            <TableCell>{row}</TableCell>
                          </TableRow>
                        ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </div>
            </div>
          </div>
        </div>
      </div>
      <ToastContainer />
    </section>
  );
}

export default DbSecrets;
