import React, { useState, useEffect } from "react";
import { Layout, Menu, Breadcrumb } from "antd";
import {
  SettingOutlined,
  UserOutlined,
  DatabaseOutlined,
  FileSearchOutlined,
  ApiOutlined,
  LeftOutlined,
  RightOutlined,
} from "@ant-design/icons";
import DashboardIcon from "@material-ui/icons/Dashboard";
import StorageIcon from "@material-ui/icons/Storage";
import firebase from "../../config/fire-conf";
import { useRouter } from "next/router";
import Link from "next/link";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { useTheme } from "@material-ui/core/styles";

const { Sider } = Layout;
const { SubMenu } = Menu;

function DashboardSidebar(props) {
  const router = useRouter();
  const theme = useTheme();
  const matchesmd = useMediaQuery(theme.breakpoints.down("md"));
  const matcheslg = useMediaQuery(theme.breakpoints.up("lg"));

  const [collapsed, setcollapsed] = useState(true);
  const [selectedKey, setselectedKey] = useState(["-1"]);

  useEffect(() => {
    firebase.auth().onAuthStateChanged((user) => {
      if (!user) {
        router.push("/");
      } else {
        //getting user wallets
        firebase.database().ref("")
      }
    });
  }, []);

  useEffect(() => {
    if (router.pathname.includes("/app/dashboard")) {
      setselectedKey(["1"]);
    }
  }, []);

  useEffect(() => {
    if (matcheslg) {
      setcollapsed(false);
    } else {
      setcollapsed(true);
    }
  }, [matcheslg]);

  const changeDBroute = (route, dbId, dbIndex) => {
    setselectedDbIndex(dbIndex);
    setselectedKey([dbId]);
    router.push(route);
  };

  return (
    <section
      className="about-area ptb-80 more-top-padding"
      style={{ paddingBottom: 0 }}
    >
      <Layout
        style={{
          minHeight: "100vh",
          backgroundColor: "#ffffff",
          borderTop: "#eaeaea 0.7px solid",
        }}
      >
        <Sider
          collapsed={collapsed}
          onCollapse={(e) => setcollapsed(e)}
          onMouseEnter={() => matchesmd && setcollapsed(false)}
          onMouseLeave={() => matchesmd && setcollapsed(true)}
        >
          <Menu theme="dark" selectedKeys={selectedKey} mode="inline">
            {!matchesmd && (
              <Menu.Item
                icon={!collapsed ? <LeftOutlined /> : <RightOutlined />}
                onClick={() => setcollapsed(!collapsed)}
              >
                <span
                  style={{
                    color: "#6e6e6e",
                    fontSize: 12,
                    position: "relative",
                    top: 2,
                  }}
                >
                  Navigation
                </span>
              </Menu.Item>
            )}{" "}
            <Menu.Item key="1" icon={<DashboardIcon />}>
              <Link href={`/app/dashboard`}>
                <a> Dashboard</a>
              </Link>
            </Menu.Item>
            <SubMenu
              key="sub1"
              icon={<DatabaseOutlined />}
              title="Wallets"
              className="submeun-anchor"
            >
              <Menu.Item>ele</Menu.Item>
            </SubMenu>
            <Menu.Item key="2" icon={<FileSearchOutlined />}>
              <Link href={`/`}>
                <a>Zaps</a>
              </Link>
            </Menu.Item>
            <Menu.Item key="6" icon={<SettingOutlined />}>
              <Link href={`/`}>
                <a>Settings</a>
              </Link>
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout" style={{ backgroundColor: "#ffffff" }}>
          {React.cloneElement(props.children, {
            // dbList: dbList,
            // selectedDbIndex,
            // setdbList: setdbList,
          })}
        </Layout>
      </Layout>
    </section>
  );
}

export default DashboardSidebar;
