import React, { useState } from "react";
import { useRouter } from "next/router";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import firebase from "../../config/fire-conf";
import IconButton from "@material-ui/core/IconButton";
import EditIcon from "@material-ui/icons/Edit";
import { ToastContainer, toast } from "react-toastify";
import * as Icon from "react-feather";
import Popper from "@material-ui/core/Popper";

const useStyles = makeStyles((theme) => ({}));

function GeneralSettings(props) {
  const router = useRouter();
  const classes = useStyles();

  const [selectedWallet, setselectedWallet] = useState(0);
  const [anchorElDbName, setAnchorElDbName] = useState(null);
  const [anchorElDbDetails, setAnchorElDbDetails] = useState(null);
  const [dbNameField, setdbNameField] = useState("");
  const [dbDetailsField, setdbDetailsField] = useState("");

  const handleClickDbNamePopup = (event) => {
    setAnchorElDbName(anchorElDbName ? null : event.currentTarget);
  };

  const handleClickDbDetailsPopup = (event) => {
    setAnchorElDbDetails(anchorElDbDetails ? null : event.currentTarget);
  };

  const updateAppName = async () => {
    if (dbNameField !== "") {
      let modefiedObj = [...props.walletListArray];
      modefiedObj[selectedWallet].dbName = dbNameField;
      props.setdbList(modefiedObj);
      //updating data in firebase
      firebase
        .firestore()
        .doc(
          "usersData/" +
            props.userDataRedux.uid +
            "/Databases/" +
            props.walletListArray[selectedWallet].dbId
        )
        .update({
          dbName: dbNameField,
        })
        .then(() => {
          toast.success("App name updated Successfully", {
            position: "bottom-left",
            autoClose: 10000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
          });
        });
    } else {
      toast.error("Please provide an app name!", {
        position: "bottom-left",
        autoClose: 10000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
      });
    }
    handleClickDbNamePopup();
  };

  const updateAppDetails = () => {
    if (dbDetailsField !== "") {
      let modefiedObj = [...props.walletListArray];
      modefiedObj[selectedWallet].details = dbDetailsField;
      props.setdbList(modefiedObj);
      //updating data in firebase
      firebase
        .firestore()
        .doc(
          "usersData/" +
            props.userDataRedux.uid +
            "/Databases/" +
            props.walletListArray[selectedWallet].dbId
        )
        .update({
          details: dbDetailsField,
        })
        .then(() => {
          toast.success("App details updated Successfully", {
            position: "bottom-left",
            autoClose: 10000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
          });
        });
    } else {
      toast.error("Please provide app details!", {
        position: "bottom-left",
        autoClose: 10000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
      });
    }
    handleClickDbDetailsPopup();
  };

  const openDbNamePopup = Boolean(anchorElDbName);
  const idDbNamePopup = openDbNamePopup ? "db-name-popper" : undefined;
  const openDbDetailsPopup = Boolean(anchorElDbDetails);
  const idDbDetailsPopup = openDbDetailsPopup ? "db-details-popper" : undefined;
  return (
    <>
      <section className="blog-details-area">
        <div className="row">
          <div className="col-lg-12 col-md-12" style={{ margin: "0px auto" }}>
            <div className="blog-details">
              <div className="article-content">
                <h5>Your Wallets</h5>

                {props.walletListArray && props.walletListArray.length > 0 ? (
                  <div className="generalSet-con-main">
                    <div className="generalSet-con1">
                      {props.walletListArray.map((ele, index) => {
                        return (
                          <div
                            style={{
                              display: "flex",
                              alignItems: "center",
                            }}
                            className={
                              selectedWallet === index
                                ? "single-box2 bg-eb6b3d dbselecednav"
                                : "single-box2 bg-eb6b3d "
                            }
                            key={ele.dbId + "nav"}
                            onClick={() => setselectedWallet(index)}
                          >
                            <div
                              className="icon"
                              style={{ cursor: "pointer", marginRight: 12 }}
                            >
                              <Icon.Server style={{ height: 18, width: 18 }} />
                            </div>
                            <div className="sett-gen-nav-nam">
                              <p
                                className="nav-set-head"
                                style={{ color: "#0a0f12" }}
                              >
                                {ele.dbName}
                              </p>
                              <p
                                style={{
                                  fontSize: 11,
                                  position: "relative",
                                  top: -4,
                                }}
                              >
                                database
                              </p>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                    <div className="generalSet-con2">
                      <div
                        style={{ marginTop: 0 }}
                        className="general-settings"
                      >
                        <h6 style={{ marginTop: 0 }}>
                          Wallet Name
                          <IconButton
                            size="small"
                            style={{
                              position: "absolute",
                              marginTop: -4,
                              marginLeft: 6,
                            }}
                            aria-describedby={idDbNamePopup}
                            onClick={(e) => {
                              setdbNameField(
                                props.walletListArray[selectedWallet] &&
                                  props.walletListArray[selectedWallet].title
                                  ? props.walletListArray[selectedWallet].title
                                  : ""
                              );
                              handleClickDbNamePopup(e);
                            }}
                          >
                            <EditIcon fontSize="small" />
                          </IconButton>
                        </h6>
                        <p>
                          {props.walletListArray[selectedWallet] &&
                          props.walletListArray[selectedWallet].title
                            ? props.walletListArray[selectedWallet].title
                            : "-"}
                        </p>

                        <h6>
                          Details
                          <IconButton
                            size="small"
                            style={{
                              position: "absolute",
                              marginTop: -4,
                              marginLeft: 6,
                            }}
                            aria-describedby={idDbDetailsPopup}
                            onClick={(e) => {
                              setdbDetailsField(
                                props.walletListArray[selectedWallet] &&
                                  props.walletListArray[selectedWallet].details
                                  ? props.walletListArray[selectedWallet].details
                                  : ""
                              );
                              handleClickDbDetailsPopup(e);
                            }}
                          >
                            <EditIcon fontSize="small" />
                          </IconButton>
                        </h6>
                        <p>
                          {props.walletListArray[selectedWallet] &&
                          props.walletListArray[selectedWallet].details
                            ? props.walletListArray[selectedWallet].details
                            : "-"}
                        </p>

                        <h6> Database Id</h6>
                        <p>
                          {props.walletListArray[selectedWallet] &&
                          props.walletListArray[selectedWallet].dbId
                            ? props.walletListArray[selectedWallet].dbId
                            : "-"}
                        </p>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="generalSet-con-main">
                    <p>You didn't have any apps yet </p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
        <ToastContainer />
      </section>
      <Popper
        id={idDbNamePopup}
        open={openDbNamePopup}
        anchorEl={anchorElDbName}
        placement="bottom"
        disablePortal={false}
        modifiers={{
          flip: {
            enabled: true,
          },
          preventOverflow: {
            enabled: true,
            boundariesElement: "scrollParent",
          },
        }}
      >
        <Paper className="poper-dbname">
          <h6 className="popHeadMain">Update App Name</h6>
          <div className="form-group" style={{ margin: "20px 0px" }}>
            <input
              type="text"
              name="DB Name"
              className="form-control"
              required
              data-error="Please enter your DB Name"
              placeholder="DB Name"
              style={{ height: 40 }}
              value={dbNameField}
              onChange={(e) => {
                setdbNameField(e.target.value);
              }}
              autoFocus
            />
          </div>
          <div className="normal-button" style={{ display: "flex" }}>
            <div style={{ marginLeft: "auto" }}>
              <button
                style={{ marginRight: 10 }}
                onClick={handleClickDbNamePopup}
              >
                Cancel
              </button>

              <button onClick={updateAppName}>Update</button>
            </div>
          </div>
        </Paper>
      </Popper>
      <Popper
        id={idDbDetailsPopup}
        open={openDbDetailsPopup}
        anchorEl={anchorElDbDetails}
        placement="bottom"
        disablePortal={false}
        modifiers={{
          flip: {
            enabled: true,
          },
          preventOverflow: {
            enabled: true,
            boundariesElement: "scrollParent",
          },
        }}
      >
        <Paper className="poper-dbname">
          <h6 className="popHeadMain">Update App Details</h6>

          <div className="form-group" style={{ margin: "20px 0px" }}>
            <textarea
              name="details"
              className="form-control"
              id="details"
              cols="30"
              rows="5"
              required
              data-error="Please provide DB details"
              placeholder="DB Details"
              value={dbDetailsField}
              onChange={(e) => {
                setdbDetailsField(e.target.value);
              }}
              autoFocus
            />
          </div>
          <div className="normal-button" style={{ display: "flex" }}>
            <div style={{ marginLeft: "auto" }}>
              <button
                style={{ marginRight: 10 }}
                onClick={handleClickDbDetailsPopup}
              >
                Cancel
              </button>

              <button onClick={updateAppDetails}>Update</button>
            </div>
          </div>
        </Paper>
      </Popper>
    </>
  );
}

export default GeneralSettings;
