import React, { useState } from "react";
import * as Icon from "react-feather";
import Link from "../common/ActiveLink";
import ShowMoreText from "react-show-more-text";
import { route } from "next/dist/next-server/server/router";
import { useRouter } from "next/router";
import NewWalletDialog from "./newWalletDialog";

function DashboardPage(props) {
  const router = useRouter();
  const [newWalletDialogState, setnewWalletDialogState] = useState();
  return (
    <>
      <section className="domain-search-area ptb-70">
        <div className="container">
          <div className="domain-search-content">
            <h2>Create your own workflow</h2>
            <form>
              <input
                type="text"
                className="form-control"
                name="domain-search"
                id="domain-search"
                placeholder="Search wallets here"
              />

              <button type="submit" style={{ marginLeft: "auto" }}>
                Search
              </button>
            </form>

            <ul
              className="domain-price dash-links"
              style={{
                display: "flex",
                alignItems: "center",
                flexWrap: "wrap",
              }}
            >
              <li>
                <Link href={`/app/-`}>
                  <a>
                    Query
                    <br />
                    Transactions
                  </a>
                </Link>
              </li>

              <li>
                <Link href={`/app/api/-`}>
                  <a>
                    Api
                    <br />
                    Docs
                  </a>
                </Link>
              </li>

              <li>
                <Link href={`/app/settings`}>
                  <a>
                    Wallet
                    <br />
                    Settings
                  </a>
                </Link>
              </li>
              <li
                className="nav-item mobile-nav-only"
                style={{ flex: "none", marginLeft: 20 }}
              >
                <Link href="/services/database-in-blockchain/create">
                  <a className="btn btn-light ">Create New Wallet</a>
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </section>

      <section className="boxes-area" style={{ margin: 0 }}>
        <div className="container">
          {props.dbList && props.dbList.length > 0 ? (
            <div className="row">
              {props.dbList.map((item, index) => {
                return (
                  <div className="col-lg-3 col-md-6" key={item.dbId}>
                    <div className="single-box bg-eb6b3d fix-height">
                      <div
                        className="icon"
                        style={{ cursor: "pointer" }}
                        onClick={() =>
                          router.push(`/app/databases/${item.dbId}`)
                        }
                      >
                        <Icon.Server />
                      </div>
                      <h3
                        style={{ cursor: "pointer" }}
                        onClick={() =>
                          router.push(`/app/databases/${item.dbId}`)
                        }
                      >
                        {item.dbName}
                      </h3>
                      <p>
                        <ShowMoreText
                          lines={3}
                          more="more"
                          less="less"
                          anchorClass="anchor-more-class"
                          expanded={false}
                        >
                          {item.details}
                        </ShowMoreText>
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div style={{ textAlign: "center" }}>
              <p>Get start by creating a new database</p>
            </div>
          )}
        </div>
      </section>

      <NewWalletDialog
        dialogState={newWalletDialogState}
        setdialogState={setnewWalletDialogState}

        uid={userDataRedux && userDataRedux.uid ? userDataRedux.uid : ""}
        setprofileImg={setprofileImg}
      />
    </>
  );
}

export default DashboardPage;
